/**
 * @file Loader
 * @author Alexander Rose <alexander.rose@weirdbyte.de>
 * @private
 */

import { ParserRegistry } from '../globals.js'
import { defaults } from '../utils.js'
import FileStreamer from '../streamer/file-streamer.js'
import NetworkStreamer from '../streamer/network-streamer.js'

/**
 * Loader parameter object.
 * @typedef {Object} LoaderParameters - loader parameters
 * @property {String} ext - file extension, determines file type
 * @property {Boolean} compressed - flag data as compressed
 * @property {Boolean} binary - flag data as binary
 * @property {String} name - set data name
 */

/**
 * Loader base class
 */
class Loader {
  /**
   * Construct a loader object
   * @param  {String|File|Blob} src - data source, string is interpreted as an URL
   * @param  {LoaderParameters} params - parameters object
   */
  constructor (src, params) {
    const p = Object.assign({}, params)

    this.compressed = defaults(p.compressed, false)
    this.binary = defaults(p.binary, ParserRegistry.isBinary(p.ext))
    this.name = defaults(p.name, '')
    this.ext = defaults(p.ext, '')
    this.dir = defaults(p.dir, '')
    this.path = defaults(p.path, '')
    this.protocol = defaults(p.protocol, '')

    this.params = params

    //

    const streamerParams = {
      compressed: this.compressed,
      binary: this.binary,
      json: ParserRegistry.isJson(this.ext),
      xml: ParserRegistry.isXml(this.ext)
    }

    if ((typeof File !== 'undefined' && src instanceof window.File) ||
        (typeof Blob !== 'undefined' && src instanceof window.Blob)
    ) {
      this.streamer = new FileStreamer(src, streamerParams)
    } else {
      this.streamer = new NetworkStreamer(src, streamerParams)
    }

    if (typeof p.onProgress === 'function') {
      this.streamer.onprogress = p.onprogress
    }
  }

  /**
   * Load data
   * @abstract
   * @return {Promise} resolves to the loaded data {@link Object}
   */
  load () {
    return Promise.reject(new Error('not implemented'))
  }
}

export default Loader

/**
 * @file Parser Loader
 * @author Alexander Rose <alexander.rose@weirdbyte.de>
 * @private
 */

import { ParserRegistry } from '../globals.js'
import Loader from './loader.js'

/**
 * Parser loader class
 * @extends Loader
 */
class ParserLoader extends Loader {
  /**
   * Load parsed object
   * @return {Promise} resolves to the loaded & parsed {@link Structure},
   *                   {@link Volume}, {@link Surface} or data object
   */
  load () {
    var ParserClass = ParserRegistry.get(this.ext)
    var parser = new ParserClass(this.streamer, this.params)

    return parser.parse()
  }
}

export default ParserLoader

/**
 * @file Plugin Loader
 * @author Alexander Rose <alexander.rose@weirdbyte.de>
 * @private
 */

import Loader from './loader.js'
import { autoLoad } from './loader-utils.js'
import Script from '../script.js'

/**
 * Plugin loader class
 * @extends Loader
 */
class PluginLoader extends Loader {
  /**
   * Load plugin
   * @return {Promise} resolves to the loaded plugin {@link Script}
   */
  load () {
    var basePath
    if (this.protocol) {
      basePath = this.protocol + '://' + this.dir
    } else {
      basePath = this.dir
    }

    return this.streamer.read().then(() => {
      var manifest = JSON.parse(this.streamer.asText())
      var promiseList = []

      manifest.files.map(function (name) {
        promiseList.push(
          autoLoad(basePath + name, {
            ext: 'text', useWorker: false
          })
        )
      })

      return Promise.all(promiseList).then(dataList => {
        var text = dataList.reduce(function (text, value) {
          return text + '\n\n' + value.data
        }, '')
        text += manifest.source || ''

        return new Script(text, this.name, this.path)
      })
    })
  }
}

export default PluginLoader

/**
 * @file Script Loader
 * @author Alexander Rose <alexander.rose@weirdbyte.de>
 * @private
 */

import Loader from './loader.js'
import Script from '../script.js'

/**
 * Script loader class
 * @extends Loader
 */
class ScriptLoader extends Loader {
  /**
   * Load script
   * @return {Promise} resolves to the loaded {@link Script}
   */
  load () {
    return this.streamer.read().then(() => {
      return new Script(
        this.streamer.asText(), this.name, this.path
      )
    })
  }
}

export default ScriptLoader

/**
 * @file Array Utils
 * @author Alexander Rose <alexander.rose@weirdbyte.de>
 * @private
 */

import { Vector3 } from '../../lib/three.es6.js'

import { TwoPI } from './math-constants.js'

function circularMean (array, max, stride, offset, indices) {
  // http://en.wikipedia.org/wiki/Center_of_mass#Systems_with_periodic_boundary_conditions

  // Bai, Linge; Breen, David (2008). Calculating Center of Mass in an Unbounded 2D Environment. Journal of Graphics, GPU, and Game Tools 13 (4): 5360.

  // http://stackoverflow.com/questions/18166507/using-fft-to-find-the-center-of-mass-under-periodic-boundary-conditions

  stride = stride || 1
  offset = offset || 0

  var n = indices ? indices.length : array.length / stride
  var angle, i, c

  var cosMean = 0
  var sinMean = 0

  if (indices) {
    for (i = 0; i < n; ++i) {
      c = (array[ indices[ i ] * stride + offset ] + max) % max

      angle = (c / max) * TwoPI - Math.PI

      cosMean += Math.cos(angle)
      sinMean += Math.sin(angle)
    }
  } else {
    for (i = offset; i < n; i += stride) {
      c = (array[ i ] + max) % max

      angle = (c / max) * TwoPI - Math.PI

      cosMean += Math.cos(angle)
      sinMean += Math.sin(angle)
    }
  }

  cosMean /= n
  sinMean /= n

  var meanAngle = Math.atan2(sinMean, cosMean)

  var mean = (meanAngle + Math.PI) / TwoPI * max

  return mean
}

function calculateCenterArray (array1, array2, center, offset) {
  var n = array1.length
  center = center || new Float32Array(n)
  offset = offset || 0

  for (var i = 0; i < n; i += 3) {
    center[ offset + i + 0 ] = (array1[ i + 0 ] + array2[ i + 0 ]) / 2.0
    center[ offset + i + 1 ] = (array1[ i + 1 ] + array2[ i + 1 ]) / 2.0
    center[ offset + i + 2 ] = (array1[ i + 2 ] + array2[ i + 2 ]) / 2.0
  }

  return center
}

function calculateDirectionArray (array1, array2) {
  var n = array1.length
  var direction = new Float32Array(n)

  for (var i = 0; i < n; i += 3) {
    direction[ i + 0 ] = array2[ i + 0 ] - array1[ i + 0 ]
    direction[ i + 1 ] = array2[ i + 1 ] - array1[ i + 1 ]
    direction[ i + 2 ] = array2[ i + 2 ] - array1[ i + 2 ]
  }

  return direction
}

function uniformArray (n, a, optionalTarget) {
  var array = optionalTarget || new Float32Array(n)

  for (var i = 0; i < n; ++i) {
    array[ i ] = a
  }

  return array
}

function uniformArray3 (n, a, b, c, optionalTarget) {
  var array = optionalTarget || new Float32Array(n * 3)

  var j

  for (var i = 0; i < n; ++i) {
    j = i * 3

    array[ j + 0 ] = a
    array[ j + 1 ] = b
    array[ j + 2 ] = c
  }

  return array
}

function centerArray3 (array, center) {
  const n = array.length
  center = center || new Vector3()

  for (let i = 0; i < n; i += 3) {
    center.x += array[ i ]
    center.y += array[ i + 1 ]
    center.z += array[ i + 2 ]
  }

  center.divideScalar(n / 3)

  return center
}

function serialArray (n) {
  var array = new Float32Array(n)

  for (var i = 0; i < n; ++i) {
    array[ i ] = i
  }

  return array
}

function serialBlockArray (n, b, offset = 0, optionalTarget) {
  const array = optionalTarget || new Float32Array(n * b)

  for (let i = 0; i < n; ++i) {
    const k = offset + i * b

    for (let j = 0; j < b; ++j) {
      array[ k + j ] = i
    }
  }

  return array
}

function randomColorArray (n) {
  var array = new Float32Array(n * 3)

  var j

  for (var i = 0; i < n; ++i) {
    j = i * 3

    array[ j + 0 ] = Math.random()
    array[ j + 1 ] = Math.random()
    array[ j + 2 ] = Math.random()
  }

  return array
}

function replicateArray3Entries (array, m) {
  var n = array.length / 3
  var repArr = new Float32Array(n * m * 3)

  for (var i = 0; i < n; ++i) {
    var v = i * 3
    var k = i * m * 3

    var a = array[ v + 0 ]
    var b = array[ v + 1 ]
    var c = array[ v + 2 ]

    for (var j = 0; j < m; ++j) {
      var l = k + j * 3

      repArr[ l + 0 ] = a
      repArr[ l + 1 ] = b
      repArr[ l + 2 ] = c
    }
  }

  return repArr
}

function calculateMeanArray (array1, array2) {
  var n = array1.length
  var mean = new Float32Array(n)

  for (var i = 0; i < n; i++) {
    mean[ i ] = (array1[ i ] + array2[ i ]) / 2.0
  }

  return mean
}

function calculateMinArray (array1, array2) {
  var n = array1.length
  var min = new Float32Array(n)

  for (var i = 0; i < n; i++) {
    min[ i ] = Math.min(array1[ i ], array2[ i ])
  }

  return min
}

function copyArray (src, dst, srcOffset, dstOffset, length) {
  for (var i = 0; i < length; ++i) {
    dst[ dstOffset + i ] = src[ srcOffset + i ]
  }
}

function copyWithin (array, srcOffset, dstOffset, length) {
  copyArray(array, array, srcOffset, dstOffset, length)
}

var swap = new Float32Array(4)
var temp = new Float32Array(4)
/**
 * quicksortIP
 * @function
 * @author Roman Bolzern <roman.bolzern@fhnw.ch>, 2013
 * @author I4DS http://www.fhnw.ch/i4ds, 2013
 * @license MIT License <http://www.opensource.org/licenses/mit-license.php>
 * @description
 * In-place quicksort for typed arrays (e.g. for Float32Array)
 * provides fast sorting
 * useful e.g. for a custom shader and/or BufferGeometry
 * Complexity: http://bigocheatsheet.com/ see Quicksort
 *
 * @example
 * points: [x, y, z, x, y, z, x, y, z, ...]
 * eleSize: 3 //because of (x, y, z)
 * orderElement: 0 //order according to x
 *
 * @param {TypedArray} arr - array to be sorted
 * @param {Integer} eleSize - element size
 * @param {Integer} orderElement - index of element used for sorting, < eleSize
 * @param {Integer} [begin] - start index for range to be sorted
 * @param {Integer} [end] - end index for range to be sorted
 * @return {TypedArray} the input array
 */
function quicksortIP (arr, eleSize, orderElement, begin, end) {
  begin = begin || 0
  end = (end || (arr.length / eleSize)) - 1

  var stack = []
  var sp = -1
  var left = begin
  var right = end
  var tmp = 0.0
  var x = 0
  var y = 0

  var swapF = function (a, b) {
    a *= eleSize; b *= eleSize
    for (y = 0; y < eleSize; y++) {
      tmp = arr[ a + y ]
      arr[ a + y ] = arr[ b + y ]
      arr[ b + y ] = tmp
    }
  }

  var i, j

  while (true) {
    if (right - left <= 25) {
      for (j = left + 1; j <= right; j++) {
        for (x = 0; x < eleSize; x++) {
          swap[ x ] = arr[ j * eleSize + x ]
        }

        i = j - 1

        while (i >= left && arr[ i * eleSize + orderElement ] > swap[ orderElement ]) {
          for (x = 0; x < eleSize; x++) {
            arr[ (i + 1) * eleSize + x ] = arr[ i * eleSize + x ]
          }
          i--
        }

        for (x = 0; x < eleSize; x++) {
          arr[ (i + 1) * eleSize + x ] = swap[ x ]
        }
      }

      if (sp === -1) break

      right = stack[ sp-- ] // ?
      left = stack[ sp-- ]
    } else {
      var median = (left + right) >> 1

      i = left + 1
      j = right

      swapF(median, i)

      if (arr[ left * eleSize + orderElement ] > arr[ right * eleSize + orderElement ]) {
        swapF(left, right)
      }

      if (arr[ i * eleSize + orderElement ] > arr[ right * eleSize + orderElement ]) {
        swapF(i, right)
      }

      if (arr[ left * eleSize + orderElement ] > arr[ i * eleSize + orderElement ]) {
        swapF(left, i)
