  parse () {
    return this.streamer.read().then(() => {
      this._beforeParse()
      this._parse()
      this._afterParse()
      return this[ this.__objName ]
    })
  }

  _parse () {}

  _beforeParse () {}

  _afterParse () {
    if (Debug) Log.log(this[ this.__objName ])
  }
