stage.loadFile( "rcsb://3pqr", {
    assembly: "BU1"
} ).then( function( o ){
    o.addRepresentation( "cartoon" );
    o.addRepresentation( "axes", {
        sele: "RET", showAxes: false, showBox: true, radius: 0.2
    } );
    o.addRepresentation( "ball+stick", { sele: "RET" } );
