import Store from './store.js'

/**
 * Atom store
 */
class AtomStore extends Store {
  get _defaultFields () {
    return [
      [ 'residueIndex', 1, 'uint32' ],
      [ 'atomTypeId', 1, 'uint16' ],

      [ 'x', 1, 'float32' ],
      [ 'y', 1, 'float32' ],
      [ 'z', 1, 'float32' ],
      [ 'serial', 1, 'int32' ],
      [ 'bfactor', 1, 'float32' ],
      [ 'altloc', 1, 'uint8' ],
      [ 'occupancy', 1, 'float32' ]
    ]
  }

  setAltloc (i, str) {
    this.altloc[ i ] = str.charCodeAt(0)
  }

  getAltloc (i) {
    const code = this.altloc[ i ]
    return code ? String.fromCharCode(code) : ''
  }
}

export default AtomStore

/**
 * @file Bond Hash
 * @author Alexander Rose <alexander.rose@weirdbyte.de>
 * @private
 */

import { Debug, Log } from '../globals.js'

class BondHash {
  constructor (bondStore, atomCount) {
    if (Debug) Log.time('BondHash init')

    const bondCount = bondStore.count
    const atomIndex1Array = bondStore.atomIndex1
    const atomIndex2Array = bondStore.atomIndex2
    const countArray = new Uint8Array(atomCount)
    const offsetArray = new Int32Array(atomCount)

    // count bonds per atom
    for (let i = 0; i < bondCount; ++i) {
      countArray[ atomIndex1Array[ i ] ] += 1
      countArray[ atomIndex2Array[ i ] ] += 1
    }

    // get offsets to atom bonds
    for (let i = 1; i < atomCount; ++i) {
      offsetArray[ i ] += offsetArray[ i - 1 ] + countArray[ i - 1 ]
    }

    // prepare index array
    const bondCount2 = bondCount * 2
    const indexArray = new Int32Array(bondCount2)
    for (let j = 0; j < bondCount2; ++j) {
      indexArray[ j ] = -1
    }

    // build index array
    for (let i = 0; i < bondCount; ++i) {
      const idx1 = atomIndex1Array[ i ]
      const idx2 = atomIndex2Array[ i ]
      let j1 = offsetArray[ idx1 ]
      while (indexArray[ j1 ] !== -1) {
        j1 += 1
      }
      indexArray[ j1 ] = i
      let j2 = offsetArray[ idx2 ]
      while (indexArray[ j2 ] !== -1) {
        j2 += 1
      }
      indexArray[ j2 ] = i
    }

    if (Debug) Log.timeEnd('BondHash init')

    this.countArray = countArray
    this.offsetArray = offsetArray
    this.indexArray = indexArray
  }
}

export default BondHash

/**
 * @file Bond Store
 * @author Alexander Rose <alexander.rose@weirdbyte.de>
 * @private
 */

import Store from './store.js'

/**
 * Bond store
 */
class BondStore extends Store {
  get _defaultFields () {
    return [
      [ 'atomIndex1', 1, 'int32' ],
      [ 'atomIndex2', 1, 'int32' ],
      [ 'bondOrder', 1, 'int8' ]
    ]
  }

  addBond (atom1, atom2, bondOrder) {
    this.growIfFull()

    const i = this.count
    const ai1 = atom1.index
    const ai2 = atom2.index

    if (ai1 < ai2) {
      this.atomIndex1[ i ] = ai1
      this.atomIndex2[ i ] = ai2
    } else {
      this.atomIndex2[ i ] = ai1
      this.atomIndex1[ i ] = ai2
    }
    if (bondOrder) this.bondOrder[ i ] = bondOrder

    this.count += 1
  }

  addBondIfConnected (atom1, atom2, bondOrder) {
    if (atom1.connectedTo(atom2)) {
      this.addBond(atom1, atom2, bondOrder)
      return true
    }

    return false
  }
}

export default BondStore

/**
 * @file Chain Store
 * @author Alexander Rose <alexander.rose@weirdbyte.de>
 * @private
 */

import Store from './store.js'

/**
 * Chain store
 */
class ChainStore extends Store {
  get _defaultFields () {
    return [
      [ 'entityIndex', 1, 'uint16' ],
      [ 'modelIndex', 1, 'uint16' ],
      [ 'residueOffset', 1, 'uint32' ],
      [ 'residueCount', 1, 'uint32' ],

      [ 'chainname', 4, 'uint8' ],
      [ 'chainid', 4, 'uint8' ]
    ]
  }

  setChainname (i, str) {
    const j = 4 * i
    this.chainname[ j ] = str.charCodeAt(0)
    this.chainname[ j + 1 ] = str.charCodeAt(1)
    this.chainname[ j + 2 ] = str.charCodeAt(2)
    this.chainname[ j + 3 ] = str.charCodeAt(3)
  }

  getChainname (i) {
    let chainname = ''
    for (let k = 0; k < 4; ++k) {
      const code = this.chainname[ 4 * i + k ]
      if (code) {
        chainname += String.fromCharCode(code)
      } else {
        break
      }
    }
    return chainname
  }

  setChainid (i, str) {
    const j = 4 * i
    this.chainid[ j ] = str.charCodeAt(0)
    this.chainid[ j + 1 ] = str.charCodeAt(1)
    this.chainid[ j + 2 ] = str.charCodeAt(2)
    this.chainid[ j + 3 ] = str.charCodeAt(3)
  }

  getChainid (i) {
    let chainid = ''
    for (let k = 0; k < 4; ++k) {
      const code = this.chainid[ 4 * i + k ]
      if (code) {
        chainid += String.fromCharCode(code)
      } else {
        break
      }
    }
    return chainid
  }
}

export default ChainStore

