  }

  var cgPolymer = function (p) {
    var localAngle = 20
    var centerDist = 2.0

    var residueStore = p.residueStore
    var offset = p.residueIndexStart

    var helixbundle = new Helixbundle(p)
    var pos = helixbundle.position

    var c1 = new Vector3()
    var c2 = new Vector3()

    for (var i = 0, il = p.residueCount; i < il; ++i) {
