    this._frameCount = 0
    this._currentFrame = -1
  }

  /**
   * Number of frames in the trajectory
   * @return {Number} count
   */
  get frameCount () {
    return this._frameCount
  }

  /**
   * Currently set frame of the trajectory
   * @return {Number} frame
   */
  get currentFrame () {
    return this._currentFrame
  }

  _init (structure) {
    this.setStructure(structure)
    this._loadFrameCount()
