    var p = parameters

    if (color) p.backgroundColor.set(color)

    setFog(p.backgroundColor)
    renderer.setClearColor(p.backgroundColor, 0)
    renderer.domElement.style.backgroundColor = p.backgroundColor.getStyle()

    requestRender()
