/**
 * @file Worker Pool
 * @author Alexander Rose <alexander.rose@weirdbyte.de>
 * @private
 */

import Worker from './worker.js'

function WorkerPool (name, maxCount) {
  maxCount = Math.min(8, maxCount || 2)

  var pool = []
  var count = 0

    // API

  this.name = name

  this.maxCount = maxCount

  this.post = function (aMessage, transferList, onmessage, onerror) {
    var worker = this.getNextWorker()
    worker.post(aMessage, transferList, onmessage, onerror)

    return this
  }

  this.terminate = function () {
    pool.forEach(function (worker) {
      worker.terminate()
    })
  }

  this.getNextWorker = function () {
    var nextWorker
    var minPending = Infinity

    for (var i = 0; i < maxCount; ++i) {
      if (i >= count) {
        nextWorker = new Worker(name)
        pool.push(nextWorker)
        count += 1
        break
      }

      var worker = pool[ i ]

      if (worker.pending === 0) {
        nextWorker = worker
        break
      } else if (worker.pending < minPending) {
        minPending = worker.pending
        nextWorker = worker
      }
    }

    return nextWorker
  }

  Object.defineProperties(this, {
    count: {
      get: function () { return count }
    }
  })
}

WorkerPool.prototype.constructor = WorkerPool

export default WorkerPool

/**
 * @file Worker Registry
 * @author Alexander Rose <alexander.rose@weirdbyte.de>
 * @private
 */

import { makeWorkerBlob } from './worker-utils.js'

class WorkerRegistry {
  constructor () {
    this.activeWorkerCount = 0

    this._funcDict = {}
    this._depsDict = {}
    this._blobDict = {}
  }

  add (name, func, deps) {
    this._funcDict[ name ] = func
    this._depsDict[ name ] = deps
  }

  get (name) {
    if (!this._blobDict[ name ]) {
      this._blobDict[ name ] = makeWorkerBlob(
                this._funcDict[ name ], this._depsDict[ name ]
            )
    }
    return this._blobDict[ name ]
  }
}

export default WorkerRegistry

/**
 * @file Worker Utils
 * @author Alexander Rose <alexander.rose@weirdbyte.de>
 * @private
 */

import { uniqueArray } from '../utils.js'

function getWorkerDeps (vars) {
  var deps = vars
  vars.forEach(function (sym) {
    if (sym.__deps) {
      Array.prototype.push.apply(deps, getWorkerDeps(sym.__deps))
    }
  })
  return deps
}

function makeWorkerString (vars) {
  var deps = uniqueArray(getWorkerDeps(vars))
  return deps.map(function (sym) {
    return sym.toString()
  }).join('\n\n\n')
}

function onmessage (e) {
  var name = e.data.__name
  var postId = e.data.__postId

  /* global self */
  if (name === undefined) {
    console.error('message __name undefined')
  } else if (self.func === undefined) {
    console.error('worker func undefined', name)
  } else {
    var callback = function (aMessage, transferList) {
      aMessage = aMessage || {}
      if (postId !== undefined) aMessage.__postId = postId

      try {
        self.postMessage(aMessage, transferList)
      } catch (error) {
        console.error('self.postMessage:', error)
        self.postMessage(aMessage)
      }
    }

    self.func(e, callback)
  }
}

function makeWorkerBlob (func, deps) {
  var str = "'use strict';\n\n" + makeWorkerString(deps)
  str += '\n\n\nself.func = ' + func.toString() + ';'
  str += '\n\n\nself.onmessage = ' + onmessage.toString() + ';'
    // console.log( str );
  return new window.Blob([ str ], { type: 'application/javascript' })
}

export {
    makeWorkerBlob
}

/**
 * @file Worker
 * @author Alexander Rose <alexander.rose@weirdbyte.de>
 * @private
 */

import { Log, Debug, WorkerRegistry } from '../globals.js'

function Worker (name) {
  var pending = 0
  var postCount = 0
  var onmessageDict = {}
  var onerrorDict = {}

  var blobUrl = window.URL.createObjectURL(WorkerRegistry.get(name))
  var worker = new window.Worker(blobUrl)

  WorkerRegistry.activeWorkerCount += 1

  worker.onmessage = function (event) {
    pending -= 1
    var postId = event.data.__postId

    if (Debug) Log.timeEnd('Worker.postMessage ' + name + ' #' + postId)

    if (onmessageDict[ postId ]) {
      onmessageDict[ postId ].call(worker, event)
    } else {
            // Log.debug( "No onmessage", postId, name );
    }

    delete onmessageDict[ postId ]
    delete onerrorDict[ postId ]
  }

  worker.onerror = function (event) {
    pending -= 1
    if (event.data) {
      var postId = event.data.__postId
      if (onerrorDict[ postId ]) {
        onerrorDict[ postId ].call(worker, event)
      } else {
        Log.error('Worker.onerror', postId, name, event)
      }
      delete onmessageDict[ postId ]
      delete onerrorDict[ postId ]
    } else {
      Log.error('Worker.onerror', name, event)
    }
  }

    // API

  this.name = name

  this.post = function (aMessage, transferList, onmessage, onerror) {
    onmessageDict[ postCount ] = onmessage
    onerrorDict[ postCount ] = onerror

    aMessage = aMessage || {}
    aMessage.__name = name
    aMessage.__postId = postCount
    aMessage.__debug = Debug

    if (Debug) Log.time('Worker.postMessage ' + name + ' #' + postCount)

    try {
      worker.postMessage(aMessage, transferList)
    } catch (error) {
      Log.error('worker.post:', error)
      worker.postMessage(aMessage)
    }

    pending += 1
    postCount += 1

    return this
  }

  this.terminate = function () {
    if (worker) {
      worker.terminate()
      window.URL.revokeObjectURL(blobUrl)
      WorkerRegistry.activeWorkerCount -= 1
    } else {
      Log.log('no worker to terminate')
    }
  }

  Object.defineProperties(this, {
    postCount: {
      get: function () { return postCount }
    },
    pending: {
      get: function () { return pending }
    }
  })
}

Worker.prototype.constructor = Worker

export default Worker

/**
 * @file Pdb Writer
 * @author Alexander Rose <alexander.rose@weirdbyte.de>
 * @private
 */

import { defaults, ensureArray } from '../utils.js'
import Writer from './writer.js'

import { sprintf } from '../../lib/sprintf.es6.js'

// http://www.wwpdb.org/documentation/file-format

// Sample PDB line, the coords X,Y,Z are fields 5,6,7 on each line.
// ATOM      1  N   ARG     1      29.292  13.212 -12.751  1.00 33.78      1BPT 108

const AtomFormat =
  'ATOM  %5d %-4s %3s %1s%4d    %8.3f%8.3f%8.3f%6.2f%6.2f      %4s%2s'

const HetatmFormat =
  'HETATM%5d %-4s %3s %1s%4d    %8.3f%8.3f%8.3f%6.2f%6.2f      %4s%2s'

/**
 * Create a PDB file from a Structure object
 */
class PdbWriter extends Writer {
  /**
   * @param  {Structure} structure - the structure object
   * @param  {Object} params - parameters]
   */
  constructor (structure, params) {
    const p = Object.assign({}, params)

    super()

    this.renumberSerial = defaults(p.renumberSerial, true)
    this.remarks = ensureArray(defaults(p.remarks, []))

    this.structure = structure
    this._records = []
  }

  get mimeType () { return 'text/plain' }
  get defaultName () { return 'structure' }
  get defaultExt () { return 'pdb' }

  _writeRecords () {
    this._records.length = 0

    this._writeTitle()
    this._writeRemarks()
    this._writeAtoms()
  }

  _writeTitle () {
    // FIXME multiline if title line longer than 80 chars
    this._records.push(sprintf('TITLE %-74s', this.structure.name))
  }

  _writeRemarks () {
    this.remarks.forEach(str => {
      this._records.push(sprintf('REMARK %-73s', str))
    })

    if (this.structure.trajectory) {
      this._records.push(sprintf(
        'REMARK %-73s',
        "Trajectory '" + this.structure.trajectory.name + "'"
      ))
      this._records.push(sprintf(
        'REMARK %-73s',
        'Frame ' + this.structure.trajectory.frame + ''
      ))
    }
  }

  _writeAtoms () {
    let ia = 1
    let im = 1

    this.structure.eachModel(m => {
      this._records.push(sprintf('MODEL %-74d', im++))

      m.eachAtom(a => {
        const formatString = a.hetero ? HetatmFormat : AtomFormat
        const serial = this.renumberSerial ? ia : a.serial

        // Alignment of one-letter atom name such as C starts at column 14,
        // while two-letter atom name such as FE starts at column 13.
        let atomname = a.atomname
        if (atomname.length === 1) atomname = ' ' + atomname

        this._records.push(sprintf(
          formatString,

          serial,
          atomname,
          a.resname,
          defaults(a.chainname, ' '),
          a.resno,
          a.x, a.y, a.z,
          defaults(a.occurence, 1.0),
          defaults(a.bfactor, 0.0),
          defaults(a.segid, ''),
          defaults(a.element, '')
        ))
        ia += 1
      })

      this._records.push(sprintf('%-80s', 'ENDMDL'))
      im += 1
    })

    this._records.push(sprintf('%-80s', 'END'))
  }

  getString () {
    console.warn('PdbWriter.getString() is deprecated, use .getData instead')
    return this.getData()
  }

  /**
   * Get string containing the PDB file data
   * @return {String} PDB file
   */
  getData () {
    this._writeRecords()
    return this._records.join('\n')
  }
}

export default PdbWriter

/**
 * Writer class for sdf/mol files.
 */

import Writer from './writer.js'

import { sprintf } from '../../lib/sprintf.es6.js'

const CountFormat =
  '%3i%3i  0  0  0  0  0  0  0  0999 V2000'
  // Hard-coded chiral as false as we don't specify it any atoms

const AtomLine =
  '%10.4f%10.4f%10.4f %-3s 0%3i  0  0  0'
  //

const BondFormat =
  '%3i%3i%3i  0  0  0'

class SdfWriter extends Writer {
  /**
   * @param {Structure} structure - structure to write
   * @param {Object} params - parameters
   */
  constructor (structure) {
    super()

    this.structure = structure
    // Follow the pdb-writer example:
    this._records = []
  }

  get idString () {
    return this.structure.id
  }

  get titleString () {
    return '  ' + this.structure.title
  }

  get countsString () {
    return sprintf(
      CountFormat,
      this.structure.atomCount,
      this.structure.bondCount
      )
  }

  get chargeLines () {
    const pairs = []
    this.structure.eachAtom(ap => {
      if (ap.formalCharge != null && ap.formalCharge !== 0) {
        pairs.push([ap.index, ap.formalCharge])
      }
    })
    const lines = []
    for (let i = 0; i < pairs.length; i += 8) {
      const nCharges = Math.min(8, pairs.length - i)
      let s = sprintf('M  CHG%3i', nCharges)
      for (let j = i; j < i + nCharges; j++) {
        s += sprintf(' %3i %3i', pairs[j][0] + 1, pairs[j][1])
      }
      lines.push(s)
    }
    return lines
  }

  formatAtom (ap) {
    let charge = 0
    if (ap.formalCharge != null && ap.formalCharge !== 0) {
      charge = 4 - ap.formalCharge
    }
    const line = sprintf(
      AtomLine, ap.x, ap.y, ap.z, ap.element, charge
    )
    if (line.length !== 48) { throw new Error('Incompatible atom for sdf format') }

    return line
  }

  formatBond (bp) {
    return sprintf(
      BondFormat,
      bp.atomIndex1 + 1,
      bp.atomIndex2 + 1,
      bp.bondOrder)
  }

  _writeRecords () {
    this._records.length = 0
    this._writeHeader()
    this._writeCTab()
    this._writeFooter()
  }

  _writeHeader () {
    this._records.push(this.idString, this.titleString, '')
  }

  _writeCTab () {
    this._records.push(this.countsString)
    this.structure.eachAtom(ap => {
      this._records.push(this.formatAtom(ap))
    })
    this.structure.eachBond(bp => {
      this._records.push(this.formatBond(bp))
    })
    this.chargeLines.forEach(line => {
      this._records.push(line)
    })
    this._records.push('M  END')
  }

  _writeFooter () {
    this._records.push('$$$$')
  }

  getData () {
    this._writeRecords()
    return this._records.join('\n')
  }
}

export default SdfWriter

/**
 * @file STL Writer
 * @author Paul Pillot <paul.pillot@cimf.ca>
 * @private
 */

import { Vector3 } from '../../lib/three.es6.js'

import Writer from './writer.js'
import IOBuffer from '../utils/io-buffer.js'

// https://en.wikipedia.org/wiki/STL_(file_format)#ASCII_STL

/**
 * Create an STL File from a surface Object (e.g. for 3D printing)
 *
 * @example
 * molsurf = new MolecularSurface(structure)
 * surf = molsurf.getSurface({type: 'av', probeRadius: 1.4})
 * stl = new StlWriter(surf)
 * stl.download('myFileName')
 */
class StlWriter extends Writer {
  /**
   * @param {Surface} surface - the surface to write out
   */
  constructor (surface) {
    super()

    this.surface = surface
    this._records = []
  }

  get mimeType () { return 'application/vnd.ms-pki.stl' }
  get defaultName () { return 'surface' }
  get defaultExt () { return 'stl' }

  /*
   * Get STL Binary data
   *
   * Adapted from: https://github.com/mrdoob/three.js/blob/master/examples/js/exporters/STLBinaryExporter.js
   * see https://en.wikipedia.org/wiki/STL_(file_format)#Binary_STL for the file format description
   *
   * @return {DataView} the data
   */
  getData () {
    const triangles = this.surface.index.length / 3
    const bufferLength = triangles * 2 + triangles * 3 * 4 * 4 + 80 + 4
    const output = new IOBuffer(bufferLength)

    output.skip(80)  // skip header
    output.writeUint32(triangles)

    const vector = new Vector3()
    const vectorNorm1 = new Vector3()
    const vectorNorm2 = new Vector3()
    const vectorNorm3 = new Vector3()

    // traversing vertices
    for (let i = 0; i < triangles; i++) {
      const indices = [
        this.surface.index[i * 3],
        this.surface.index[i * 3 + 1],
        this.surface.index[i * 3 + 2]
      ]

      vectorNorm1.fromArray(this.surface.normal, indices[0] * 3)
      vectorNorm2.fromArray(this.surface.normal, indices[1] * 3)
      vectorNorm3.fromArray(this.surface.normal, indices[2] * 3)

      vector.addVectors(vectorNorm1, vectorNorm2).add(vectorNorm3).normalize()

      output.writeFloat32(vector.x)
      output.writeFloat32(vector.y)
      output.writeFloat32(vector.z)

      for (let j = 0; j < 3; j++) {
        vector.fromArray(this.surface.position, indices[j] * 3)

        output.writeFloat32(vector.x)  // vertices
        output.writeFloat32(vector.y)
        output.writeFloat32(vector.z)
      }

      output.writeUint16(0)  // attribute byte count
    }

    return new DataView(output.buffer)
  }
}

export default StlWriter

/**
 * @file Writer
 * @author Alexander Rose <alexander.rose@weirdbyte.de>
 * @private
 */

import { defaults, download } from '../utils.js'

/**
 * Base class for writers
 * @interface
 */
class Writer {
  /**
   * @abstract
   * @return {String} the default mime type
   */
  get mimeType () {}

  /**
   * @abstract
   * @return {String} the default file name
   */
  get defaultName () {}

  /**
   * @abstract
   * @return {String} the default file extension
   */
  get defaultExt () {}

  /**
   * @abstract
   * @return {Anything} the data to be written
   */
  getData () {}

  /**
   * Get a blob with the written data
   * @return {Blob} the blob
   */
  getBlob () {
    return new window.Blob([ this.getData() ], { type: this.mimeType })
  }

  /**
   * Trigger a download of the
   * @param  {[type]} name [description]
   * @param  {[type]} ext  [description]
   * @return {[type]}      [description]
   */
  download (name, ext) {
    name = defaults(name, this.defaultName)
    ext = defaults(ext, this.defaultExt)

    download(this.getBlob(), `${name}.${ext}`)
  }
}

export default Writer

# Coloring

## Representations

```
stage.loadFile("rcsb://1crn").then(function (o) {
  o.addRepresentation("ball+stick", {colorScheme: "bfactor"});
  o.autoView();
});
```


